// Need to convert these to object constructors later

var userTemplate = [
	'USER_NAME',
	'PASSWORD',
	'DATE_REGISTERED'
];

var personTemplate = [
	'USER_NAME',
	'FIRST_NAME', 
	'LAST_NAME', 
	'ADDRESS', 
	'EMAIL', 
	'PHONE'
];

function assignObj(template, list) {
	var json = {};

	for (var i = 0; i < template.length; i++) {
		json[template[i]] = list[i];
	}

	return json;
}

function createUser(userList) {
	return assignObj(userTemplate, userList);
}

function createPerson(personList) {
	return assignObj(personTemplate, personList);
}

exports.createUser = createUser;
exports.createPerson = createPerson;
